sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	initialize: function(){

	},
	isDateToday: function(strDate){

		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		// Get the date portion of rightnow and testdate (no timestamp)
		var today = rightnow.getLocalDate();
		var istoday = testdate.getLocalDate();

		// Compare today and istoday to see if they are the same day
		return today.compareTo(istoday) == 0;
	},
	isDatePast: function(strDate){

		gs.info('isDatePast: ' + strDate);

		// rightnow stores the current time
		var rightnow = new GlideDateTime();
		// Create a GlideDateTime object for the When needed date
		var whenNeeded = new GlideDateTime(strDate);

		// If the When needed date is before rightnow, do not write the record to the database
		// Output an error message to the screen
		gs.info('whenNeeded.before(rightnow): ' + whenNeeded.before(rightnow));
		return whenNeeded.before(rightnow);
	},
	type: 'NeedItUtils'
});
